import React, { Component } from 'react';

class Counter extends Component {
    state = {
        count: 0,
        tags: []
        //imageUrl: 'https://picsum.photos/200'
    };

    //styles = {
    //    fontSize: 10,
    //    fontWeight: "bold"
    //};

    renderTags() {
        if (this.state.tags.length === 0) return <p>There are no tags!</p>;

        return <ul>{this.state.tags.map(tag => <li key={tag}>{tag}</li>)}</ul>;
    }

    //render() {
    //    return (
    //        <div>
    //            { this.state.tags.length === 0 && "Please create a new tag!"}
    //            {this.renderTags()}
    //        </div>
    //    );
    //}

    //constructor() {
    //    super();
    //    this.handleIncrement = this.handleIncrement.bind(this); //returns a new instance of this function
    //}

    //arrow function acts the same way as a constructor so you have access to the child component/states
    handleIncrement = product => {
        console.log(product)
        this.setState({ count: this.state.count + 1 }) //setState tells the DOM something is going to change like TF
        console.log("Increment Clicked", this);
    };

    doHandleIncrement = () => {
        this.handleIncrement({ id: 1});
    }

    render() { 
        return ( 
            <React.Fragment>
                <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
                <button onClick={() => this.handleIncrement(product)} className='btn btn-secondary btn-sm'>Increment</button>
                <ul>
                    { this.state.tags.map(tag => <li key={tag}>{ tag }</li>) }
                </ul>
            </React.Fragment>
        );    
    }

    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += this.state.count === 0 ? "warning" : "primary";
        return classes;
    }

    formatCount() {
        const { count } = this.state;
        return count === 0 ? "Zero" : count;
    }
}

export default Counter;
